let appVersion= 22;
let initted = false;
let postInitJobs = [];
function messageHandler(message) {
    if (message.data.action === 'NIGHT') {
        night = message.data.value;
    }
    if (socket.readyState === WebSocket.OPEN) {
        socket.sendObject(message.data);
    }
}

self.addEventListener('message', async (message) => {
    if (message.data.action === 'INIT') {
        port = message.data.port;
        appVersion=parseInt(message.data.appVersion);
        
        console.log("[WORKER] Initializing with canvas dimensions: " + message.data.width + "x" + message.data.height);

        let useBroadway = message.data.broadway;

        if(!useBroadway) {
            const codecWorking = await isWebCodecsWorkingWithDecode();
            if(codecWorking) {
                console.log("webcodec functional");
            } else {
                console.log("webcodec broken");
                useBroadway = true;
            }
        }

        width = message.data.width;
        height = message.data.height;
        console.log("üìê DIMENSIONS INITIALES WORKER: " + width + "x" + height);
        
        initCanvas(message.data.canvas, useBroadway);
        initted = true;
        if(postInitJobs.length > 0){
            postInitJobs.forEach(msg => messageHandler(msg));
            postInitJobs = []
        }
    } else if (message.data.action === 'RESIZE') {
        // Gestion du changement de r√©solution
        console.log("[WORKER] Resizing decoder from " + width + "x" + height + " to " + message.data.width + "x" + message.data.height);
        
        // Log d√©taill√© pour le redimensionnement
        console.log("üìê REDIMENSIONNEMENT D√âCODEUR D√âTAILL√â: " + 
                   "\n  - Avant: " + width + "x" + height + 
                   "\n  - Apr√®s: " + message.data.width + "x" + message.data.height +
                   "\n  - Canvas actuel: " + (gl ? gl.drawingBufferWidth + "x" + gl.drawingBufferHeight : "N/A") +
                   "\n  - Derni√®re frame: " + lastFrameWidth + "x" + lastFrameHeight);
        
        width = message.data.width;
        height = message.data.height;
        
        // Mettre √† jour la configuration du d√©codeur si nous utilisons WebCodec
        if(decoder !== null && decoder.state !== 'closed') {
            try {
                // Reconfigurer le d√©codeur avec les nouvelles dimensions
                let config = {
                    codec: "avc1.",
                    codedHeight: height,
                    codedWidth: width,
                };
                
                // Ajouter le codec sp√©cifique si nous l'avons d√©j√†
                if (sps && sps.length > 7) {
                    for (let i = 5; i < 8; ++i) {
                        var h = sps[i].toString(16);
                        if (h.length < 2) {
                            h = '0' + h;
                        }
                        config.codec += h;
                    }
                } else {
                    // Codec par d√©faut si on n'a pas encore re√ßu de SPS
                    config.codec += "42002a";
                }
                
                console.log("[WORKER] Reconfiguring decoder with:", config);
                decoder.configure(config);
                
                self.postMessage({warning: "R√©solution adapt√©e, en attente d'une nouvelle image cl√©..."});
            } catch (e) {
                console.error("[WORKER] Error reconfiguring decoder:", e);
                self.postMessage({error: "Erreur lors du changement de r√©solution: " + e.message});
                // En cas d'erreur, essayer de basculer vers Broadway
                switchToBroadway();
            }
        } else if (broadwayDecoder !== null) {
            // Pour Broadway, nous n'avons pas besoin de reconfiguration explicite
            // Le d√©codeur s'ajustera automatiquement √† la nouvelle r√©solution
            console.log("[WORKER] Broadway decoder will automatically adjust to new resolution on next keyframe");
            self.postMessage({warning: "En attente d'une nouvelle image cl√©..."});
        }
        
        // Ajuster le viewport WebGL
        if (gl) {
            console.log("[WORKER] Adjusting WebGL viewport to " + width + "x" + height);
            console.log("üìê DONN√âES GL AVANT AJUSTEMENT: " + 
                      "Viewport: " + gl.getParameter(gl.VIEWPORT) + 
                      ", DrawingBuffer: " + gl.drawingBufferWidth + "x" + gl.drawingBufferHeight);
            
            gl.viewport(0, 0, width, height);
            
            console.log("üìê DONN√âES GL APR√àS AJUSTEMENT: " + 
                      "Viewport: " + gl.getParameter(gl.VIEWPORT) + 
                      ", DrawingBuffer: " + gl.drawingBufferWidth + "x" + gl.drawingBufferHeight);
        }
    } else if (message.data.action === 'CLEAR_BUFFERS') {
        // Vider les tampons de frames en attente
        console.log("[WORKER] Clearing pending frames buffer, had " + pendingFrames.length + " frames");
        
        // Nettoyer en conservant √©ventuellement la derni√®re frame pour √©viter l'√©cran noir
        if (pendingFrames.length > 0) {
            const lastFrame = pendingFrames[pendingFrames.length - 1];
            pendingFrames = [];
            if (lastFrame) {
                pendingFrames.push(lastFrame);
            }
        } else {
            pendingFrames = [];
        }
        
        // R√©initialiser l'√©tat du d√©codeur si n√©cessaire
        underflow = pendingFrames.length === 0;
        
        // Demander une nouvelle frame-cl√©
        if (socket && socket.readyState === WebSocket.OPEN) {
            console.log("[WORKER] Requesting keyframe after buffer clear");
            keyframeRequested = true;
            keyframeRequestTimestamp = Date.now();
            self.postMessage({keyframeRequested: true});
            socket.sendObject({action: "REQUEST_KEYFRAME"});
        }
    } else if (message.data.action === 'REQUEST_KEYFRAME') {
        // Demande explicite de keyframe
        console.log("üîë DEMANDE EXPLICITE DE KEYFRAME");
        if (socket && socket.readyState === WebSocket.OPEN) {
            keyframeRequested = true;
            keyframeRequestTimestamp = Date.now();
            self.postMessage({keyframeRequested: true});
            socket.sendObject({action: "REQUEST_KEYFRAME"});
        } else {
            console.warn("‚ö†Ô∏è Impossible de demander une keyframe: socket non disponible");
            self.postMessage({warning: "Impossible de demander une keyframe: socket non disponible"});
        }
    } else if(!initted) {
        postInitJobs.push(message);
    } else {
        messageHandler(message)
    }
});